sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    var requestBody = request.body.data;

    try {
        // Validate required fields
        if (!requestBody.risk_title || !requestBody.agent_id) {
            response.setStatus(400);
            response.setBody({
                error: "Missing required fields",
                required: ["risk_title", "agent_id"]
            });
            return;
        }

        // Verify agent exists
        var agentGr = new GlideRecord('x_1520788_starsh_1_agent');
        if (!agentGr.get(requestBody.agent_id)) {
            response.setStatus(404);
            response.setBody({
                error: "Agent not found",
                agent_id: requestBody.agent_id
            });
            return;
        }

        // Create risk record
        var riskGr = new GlideRecord('x_1520788_starsh_1_risk');
        riskGr.initialize();

        riskGr.setValue('risk_title', requestBody.risk_title);
        riskGr.setValue('description', requestBody.description || '');
        riskGr.setValue('identified_by', requestBody.agent_id);
        riskGr.setValue('category', requestBody.category || 'technical');
        riskGr.setValue('probability', requestBody.probability || 3);
        riskGr.setValue('impact', requestBody.impact || 3);
        riskGr.setValue('mitigation_strategy', requestBody.mitigation_strategy || '');
        riskGr.setValue('owner_agent', requestBody.owner_agent || requestBody.agent_id);
        riskGr.setValue('status', 'identified');

        // Calculate risk score
        var probability = parseInt(requestBody.probability || 3);
        var impact = parseInt(requestBody.impact || 3);
        var riskScore = probability * impact;
        riskGr.setValue('risk_score', riskScore);

        var riskSysId = riskGr.insert();

        // Determine severity message
        var severityMessage = "";
        if (riskScore >= 20) {
            severityMessage = "CRITICAL RISK - Immediate attention required";
        } else if (riskScore >= 15) {
            severityMessage = "HIGH RISK - Senior management notification";
        } else if (riskScore >= 10) {
            severityMessage = "MEDIUM RISK - Department head review";
        } else {
            severityMessage = "LOW RISK - Monitor and track";
        }

        response.setStatus(201);
        response.setBody({
            message: "Risk reported successfully",
            risk_id: riskSysId,
            risk_number: riskGr.getValue('number'),
            risk_score: riskScore,
            severity: severityMessage,
            identified_by: agentGr.getValue('agent_name')
        });

    } catch (ex) {
        response.setStatus(500);
        response.setBody({
            error: "Internal server error",
            message: ex.message
        });
    }

})(request, response);
