sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    var agentId = request.pathParams.agent_id;
    var status = request.queryParams.status || 'open'; // Default to open tasks

    try {
        // Verify agent exists
        var agentGr = new GlideRecord('x_1520788_starsh_1_agent');
        if (!agentGr.get(agentId)) {
            response.setStatus(404);
            response.setBody({
                error: "Agent not found",
                agent_id: agentId
            });
            return;
        }

        // Query tasks assigned to this agent
        var taskGr = new GlideRecord('x_1520788_starsh_1_task');
        taskGr.addQuery('assigned_agent', agentId);

        // Filter by status if provided
        if (status === 'open') {
            taskGr.addQuery('state', 'IN', '1,2'); // Open or Work in Progress
        } else {
            taskGr.addQuery('state', status);
        }

        taskGr.orderBy('priority');
        taskGr.orderByDesc('sys_created_on');
        taskGr.query();

        var tasks = [];

        while (taskGr.next()) {
            tasks.push({
                sys_id: taskGr.getUniqueValue(),
                number: taskGr.getValue('number'),
                short_description: taskGr.getValue('short_description'),
                description: taskGr.getValue('description'),
                priority: taskGr.getValue('priority'),
                state: taskGr.getValue('state'),
                task_complexity: taskGr.getValue('task_complexity'),
                due_date: taskGr.getValue('due_date'),
                technical_specifications: taskGr.getValue('technical_specifications'),
                success_criteria: taskGr.getValue('success_criteria'),
                related_design: taskGr.getValue('related_design')
            });
        }

        response.setStatus(200);
        response.setBody({
            agent_id: agentId,
            agent_name: agentGr.getValue('agent_name'),
            task_count: tasks.length,
            tasks: tasks
        });

    } catch (ex) {
        response.setStatus(500);
        response.setBody({
            error: "Internal server error",
            message: ex.message
        });
    }

})(request, response);
