sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    var taskId = request.pathParams.task_id;
    var requestBody = request.body.data;

    try {
        // Find the task
        var taskGr = new GlideRecord('x_1520788_starsh_1_task');
        if (!taskGr.get(taskId)) {
            response.setStatus(404);
            response.setBody({
                error: "Task not found",
                task_id: taskId
            });
            return;
        }

        // Update task fields
        if (requestBody.state) {
            taskGr.setValue('state', requestBody.state);
        }

        if (requestBody.work_notes) {
            taskGr.setValue('work_notes', requestBody.work_notes);
        }

        if (requestBody.percent_complete) {
            taskGr.setValue('percent_complete', requestBody.percent_complete);
        }

        if (requestBody.close_notes && requestBody.state === '3') { // State 3 = Closed
            taskGr.setValue('close_notes', requestBody.close_notes);
        }

        taskGr.update();

        response.setStatus(200);
        response.setBody({
            message: "Task updated successfully",
            task_id: taskId,
            task_number: taskGr.getValue('number'),
            state: taskGr.getValue('state'),
            percent_complete: taskGr.getValue('percent_complete')
        });

    } catch (ex) {
        response.setStatus(500);
        response.setBody({
            error: "Internal server error",
            message: ex.message
        });
    }

})(request, response);
