sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    // Get agent_id from path parameter
    var agentId = request.pathParams.agent_id;

    // Get data from request body
    var requestBody = request.body.data;

    try {
        // Find the agent
        var agentGr = new GlideRecord('x_1520788_starsh_1_agent');
        if (!agentGr.get(agentId)) {
            response.setStatus(404);
            response.setBody({
                error: "Agent not found",
                agent_id: agentId
            });
            return;
        }

        // Update agent heartbeat and status
        agentGr.setValue('last_heartbeat', new GlideDateTime());
        agentGr.setValue('status', 'active');

        // Update performance score if provided
        if (requestBody.performance_score) {
            agentGr.setValue('performance_score', requestBody.performance_score);
        }

        agentGr.update();

        // Return success
        response.setStatus(200);
        response.setBody({
            message: "Heartbeat recorded",
            agent_id: agentId,
            agent_name: agentGr.getValue('agent_name'),
            timestamp: new GlideDateTime().getDisplayValue()
        });

    } catch (ex) {
        response.setStatus(500);
        response.setBody({
            error: "Internal server error",
            message: ex.message
        });
    }

})(request, response);
